Get-ChildItem -Path "C:\\Users\\markb\\Desktop\\genny\\demos" -Recurse -Filter "*.html" | ForEach-Object {
    $content = Get-Content $_.FullName -Raw
    if \($content -match 'bg-gray-900 text-gray-300 mt-16'\) {
        $updated = $content -replace 'bg-gray-900 text-gray-300 mt-16', 'bg-gray-900 text-gray-300'
        Set-Content -Path $_.FullName -Value $updated -NoNewline
        Write-Host "Updated: $\($_.FullName\)"
    } else {
        Write-Host "No match: $\($_.FullName\)"
    }
}
ENDOFSCRIPT)
